func MaxArea(height []int) int {
	maxArea := 0
	curArea := 0
	leftI := 0
	rightI := len(height)
	for i := 0; i < (len(height)-1)/2; i++ {
		if height[leftI] >= height[rightI] {
			curArea = height[rightI] * (rightI - leftI)
		} else {
			curArea = height[leftI] * (rightI - leftI)
		}
		if maxArea < curArea {
			maxArea = curArea
		}
	}

	return maxArea
}

